var Codec = require('io/codec');
var SendFilter = require('../../../features/messages').sendFilter;

var serialiser = {
    read : function(input) {
        var filterString = Codec.readString(input);
        return SendFilter(filterString);
    },
    write : function(out, value) {
        Codec.writeString(out, value.filterString);
    }
};

module.exports = serialiser;
    
